import React, { useState } from "react";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [result, setResult] = useState(null);

  const handleSubmit = async () => {
    if (!file || !jobDesc) {
      alert("Upload resume and enter job description");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("job_description", jobDesc);

    const response = await fetch("http://127.0.0.1:8000/analyze/", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    setResult(data);
  };

  return (
    <div className="container">
      <h1>ðŸš€ AI Resume Analyzer</h1>

      <input type="file" onChange={(e) => setFile(e.target.files[0])} />

      <textarea
        placeholder="Paste Job Description Here..."
        value={jobDesc}
        onChange={(e) => setJobDesc(e.target.value)}
      />

      <button onClick={handleSubmit}>Analyze Resume</button>

      {result && (
        <div className="result">
          <h2>Final Score: {result.final_score}%</h2>
          <p>Required Skills: {result.required_count}</p>
          <p>Matched: {result.matched_count}</p>
          <p>Missing: {result.missing_count}</p>
        </div>
      )}
    </div>
  );
}

export default App;
