import React, { useState } from "react";
import "./App.css";

function App() {
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [result, setResult] = useState(null);
  const [history, setHistory] = useState([]);

  const handleFileChange = (e) => {
    setResumeFile(e.target.files[0]);
  };

  const handleAnalyze = async () => {
    if (!resumeFile || !jobDescription) {
      alert("Please upload resume and enter job description");
      return;
    }

    const formData = new FormData();
    formData.append("file", resumeFile);
    formData.append("job_description", jobDescription);

    try {
      const response = await fetch("http://127.0.0.1:8000/analyze", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult(data);

      // fetch updated history
      const historyResponse = await fetch("http://127.0.0.1:8000/history");
      const historyData = await historyResponse.json();

      setHistory(Array.isArray(historyData) ? historyData : []);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="App">
      <div className="container">
        <h1>ðŸš€ AI Resume Analyzer</h1>

        <input type="file" onChange={handleFileChange} />
        <textarea
          placeholder="Paste Job Description Here..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />

        <button onClick={handleAnalyze}>Analyze Resume</button>

        {result && (
          <div className="result-box">
            <h2>Final Match Score</h2>
            <h1>{result.final_score}%</h1>

            <h3>Score Breakdown</h3>
            <p>Semantic Match: {result.semantic_score}%</p>
            <p>Skill Match: {result.skill_score}%</p>

            <h3>Skill Breakdown</h3>
            <p>Required: {result.required_skills}</p>
            <p>Matched: {result.matched_skills}</p>
            <p>Missing: {result.missing_skills}</p>
          </div>
        )}

        <div className="history-box">
          <h2>ðŸ“Š Analysis History</h2>

          {history.length === 0 && <p>No history yet</p>}

          {history.map((item, index) => (
            <div key={index} className="history-item">
              <span>{item.filename}</span>
              <span>{item.score}%</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
