import React, { useState } from "react";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [result, setResult] = useState(null);
  const [history, setHistory] = useState([]);

  const handleSubmit = async () => {
    if (!file || !jobDescription) {
      alert("Upload resume and paste job description!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("job_description", jobDescription);

    const response = await fetch("http://127.0.0.1:8000/analyze", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    setResult(data);
    setHistory([data, ...history]);
  };

  return (
    <div className="app">
      <div className="card">
        <h1>ðŸš€ AI Resume Analyzer</h1>

        <input type="file" onChange={(e) => setFile(e.target.files[0])} />

        <textarea
          placeholder="Paste Job Description Here..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />

        <button onClick={handleSubmit}>Analyze Resume</button>

        {result && (
          <div className="result">
            <h2>{result.final_score}%</h2>

            <div className="progress">
              <p>Semantic Match</p>
              <div className="bar">
                <div
                  className="fill"
                  style={{ width: result.semantic_score + "%" }}
                ></div>
              </div>

              <p>Skill Match</p>
              <div className="bar">
                <div
                  className="fill"
                  style={{ width: result.skill_score + "%" }}
                ></div>
              </div>
            </div>

            <p>Required Skills: {result.required_skills}</p>
            <p>Matched: {result.matched_skills}</p>
            <p>Missing: {result.missing_skills}</p>
          </div>
        )}

        <div className="history">
          <h3>ðŸ“Š Analysis History</h3>
          {history.length === 0 && <p>No history yet</p>}
          {history.map((item, index) => (
            <div key={index} className="history-item">
              Score: {item.final_score}%
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
