import { useState } from "react";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleAnalyze = async () => {
    if (!file || !jobDesc) {
      alert("Please upload resume and enter job description");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("job_description", jobDesc);

    try {
      setLoading(true);

      const response = await fetch("http://127.0.0.1:8000/analyze", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error("Error:", error);
      alert("Something went wrong. Check backend.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="title">ðŸš€ AI Resume Analyzer</h1>
        <p className="subtitle">
          AI-powered resume analysis for smarter job applications
        </p>

        {/* Upload Button */}
        <label className="upload-btn">
          Upload Resume
          <input type="file" accept=".pdf" hidden onChange={handleFileChange} />
        </label>

        {file && <p className="file-name">{file.name}</p>}

        {/* Job Description */}
        <textarea
          placeholder="Paste Job Description Here..."
          value={jobDesc}
          onChange={(e) => setJobDesc(e.target.value)}
        />

        {/* Analyze Button */}
        <button className="analyze-btn" onClick={handleAnalyze}>
          {loading ? "Analyzing..." : "Analyze Resume"}
        </button>

        {/* RESULT SECTION */}
        {result && (
          <div className="result-box">
            <div className="result-title">Final Match Result</div>

            <div className="match-score">
              {result.match_score || 0}%
            </div>

            <p>Semantic Match: {result.semantic_score || 0}%</p>
            <p>Skill Match: {result.skill_score || 0}%</p>

            <div className="section-title">Matched Skills</div>
            <ul className="skill-list">
              {(result.matched_skills || []).map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>

            <div className="section-title">Missing Skills</div>
            <ul className="skill-list">
              {(result.missing_skills || []).map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
